--
-- Structure de la table `course_documents`
--

DROP TABLE IF EXISTS `course_documents`;
CREATE TABLE IF NOT EXISTS `course_documents` (
  `id` bigint UNSIGNED NOT NULL AUTO_INCREMENT,
  `uuid` char(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `course_uuid` varchar(191) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `name` varchar(191) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `document_type` enum('template','uploaded_file','custom_builder') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT 'uploaded_file',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci,
  `category` enum('apprenant','formateur','entreprise') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `file_url` varchar(191) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `file_name` varchar(191) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `file_size` bigint DEFAULT NULL,
  `audience_type` enum('students','instructors','organization') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'students',
  `position` int NOT NULL DEFAULT '0',
  `is_certificate` tinyint(1) NOT NULL DEFAULT '0',
  `certificate_background_url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `certificate_orientation` enum('portrait','landscape') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'landscape',
  `is_questionnaire` tinyint(1) NOT NULL DEFAULT '0',
  `questionnaire_type` enum('pre_course','post_course','mid_course','custom') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `created_by` bigint UNSIGNED DEFAULT NULL,
  `is_required` tinyint(1) NOT NULL DEFAULT '0',
  `template_id` bigint UNSIGNED DEFAULT NULL,
  `template_variables` json DEFAULT NULL,
  `custom_template` json DEFAULT NULL,
  `questions` json DEFAULT NULL,
  `is_generated` tinyint(1) NOT NULL DEFAULT '0',
  `generated_at` timestamp NULL DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `course_documents_uuid_unique` (`uuid`),
  KEY `course_documents_course_uuid_category_index` (`course_uuid`,`category`),
  KEY `course_documents_template_id_foreign` (`template_id`),
  KEY `course_documents_created_by_foreign` (`created_by`)
) ENGINE=MyISAM AUTO_INCREMENT=83 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

